import React, {useState,  useEffect} from "react";
import {api} from '../utils/Api'
function Main(props) {

    const [userName, setUserName] = useState('Загрузка данных...')
    const [userDescription, setUserDescription] = useState('Загрузка данных...')
    const [userAvatar, setUserAvatar] = useState('#')
    const [cards, setCards] = useState([])

    useEffect(() => {
        api.getUserData()
            .then(res => {
                setUserName(res.name);
                setUserDescription(res.about);
                setUserAvatar(res.avatar);
            })
            .catch((err) => console.log(err));

    }, []);

useEffect(() => {
    api.getInitialCards()
        .then(res => {
            setCards([...res])
        })
        .catch((err) => console.log(err));
})
    return (
        <main>
            <section className="profile">
                <button onClick={props.onEditAvatar} type="button" aria-label="Сменить аватар" className="profile__avatar-button">
                    <img src={userAvatar} alt="Фото" className="profile__picture"/>
                </button>
                <div className="profile__info">
                    <div className="profile__edit-info">
                        <h1 className="profile__name">{userName}</h1>
                        <button onClick={props.onEditProfile} type="button" aria-label="Редактировать" className="profile__edit-button"></button>
                    </div>
                    <h2 className="profile__about">{userDescription}</h2>
                </div>
                <button onClick={props.onAddPlace} type="button" aria-label="Добавить" className="profile__add-button"></button>
            </section>
            <section className="elements">
                <ul className="elements__container">
                    {cards.map((card) => (
                        // <template className="cards-template">
                        <li key={card.id} className="element">
                        <button type="button" aria-label="Удалить" className="element__delete element__delete_type_white"></button>
                        <div className="element__fallback">
                        {/*<button type="button" aria-label="Удалить" className="element__delete element__delete_type_black"></button>*/}
                        <img src={card.link} className="element__picture" alt="Фото."/>
                        </div>
                        <div className="element__info">
                        <h2 className="element__text">{card.name}</h2>
                        <div className="element__like">
                        <button type="button" aria-label="Лайк" className="element__icon"></button>
                        <p className="element__counter"></p>
                        </div>
                        </div>
                        </li>
                        // </template>
                    ))
                    }
                </ul>
            </section>
        </main>
    )
}
export default Main;
