function Main() {
  const  handleEditAvatarClick = () => {
      document.querySelector('.profile__avatar-button').addEventListener('click', () =>
      {document.querySelector('.popup_type_profile-picture').classList.add('popup_opened')});
  }
    handleEditAvatarClick();

    const handleEditProfileClick = () => {
        document.querySelector('.profile__edit-button').addEventListener('click', () =>
        {document.querySelector('.popup_type_profile').classList.add('popup_opened')});
    }
    handleEditProfileClick()
    const handleAddPlaceClick = () => {
        document.querySelector('.profile__add-button').addEventListener('click', () =>
        {document.querySelector('.popup_type_card').classList.add('popup_opened')});
    }
    handleAddPlaceClick()

    return (
        <main>
            <section className="profile">
                <button type="button" aria-label="Сменить аватар" className="profile__avatar-button">
                    <img src="#" alt="Фото" className="profile__picture"/>
                </button>
                <div className="profile__info">
                    <div className="profile__edit-info">
                        <h1 className="profile__name">Загрузка данных...</h1>
                        <button type="button" aria-label="Редактировать" className="profile__edit-button"></button>
                    </div>
                    <h2 className="profile__about">Загрузка данных...</h2>
                </div>
                <button type="button" aria-label="Добавить" className="profile__add-button"></button>
            </section>
            <section className="elements">
                <ul className="elements__container">
                </ul>
            </section>
        </main>
    )
}
export default Main;
